    public function __construct(string $database, string $schema, string $table)
    {
        $conn = DB::connection($database);
        $conn->getPdo()->exec('ALTER SESSION SET CURRENT_SCHEMA = ' . $schema);
        $this->schemaManager = $conn->getDoctrineSchemaManager();
        $this->table = $this->schemaManager->introspectTable($table);

        $this->database = $database;
        $this->schema = $schema;
    }

    public function title(): string
    {
        $result = "{{서비스코드}}-DE-DT-" . str_pad(array_search($this->table->getName(), $this->tableNames), 6, '0', STR_PAD_LEFT);

        return $result;
    }

    public function table(): string
    {
        $primaryKey = $this->table->getPrimaryKey()->getColumns();

        $result = "| 컬럼명 | 타입 | 길이 | 정밀도 | 스케일 | 기본값 | 널허용 | PK | 설명 |" . PHP_EOL
            .  "| --- | --- | --- | --- | --- | --- | --- | --- | --- |" . PHP_EOL;

        $columns = DB::select("SELECT a.column_name, a.data_type, a.data_length, a.data_precision, a.data_scale, a.data_default, a.nullable, b.comments FROM all_tab_columns a "
            . "inner join all_col_comments b on a.owner=b.owner and a.table_name=b.table_name and a.column_name=b.column_name "
            . "where a.table_name = 'PR_GOODS_BASE' AND a.owner = 'EC_MGR'");

        foreach ($columns as $column) {
            $result .= "| " . $column->column_name
                . " | " . $column->data_type
                . " | " . $column->data_length
                . " | " . $column->data_precision
                . " | " . $column->data_scale
                . " | " . $column->data_default
                . " | " . ($column->nullable === "Y" ? "O" : "X")
                . " | " . (in_array($column->column_name, $primaryKey) ? 'O' : 'X')
                . " | " . preg_replace('/\r|\n/', '<br>', $column->comments)
                . " |" . PHP_EOL;
        }

        return $result;
    }

    public function indexes(): string
    {
        $result = "| 이름                  | 컬럼           | PK     | UNIQUE |" . PHP_EOL
            . "| ------------------- | ------------ | ------ | ------ |" . PHP_EOL;

        foreach ($this->table->getIndexes() as $index) {
            $result .=  "| " . $index->getName() . " "
                . "| " . implode(",", $index->getColumns()) . " "
                . "| " . ($index->isPrimary() ? "O" : "X") . " "
                . "| " . ($index->isUnique() ? "O" : "X") . "|" . PHP_EOL;
        }
        $result .= PHP_EOL;

        return $result;
    }

    public function constraints(): string
    {
        $result = "| 이름 | 컬럼 | 타입 | 조건 |" . PHP_EOL
            . "| --- | --- | --- | --- |" . PHP_EOL;


        $constraints = DB::select("SELECT
    a.constraint_name, b.column_name, a.constraint_type, a.search_condition
FROM
    ALL_CONSTRAINTS a
        JOIN ALL_CONS_COLUMNS b ON a.constraint_name = b.constraint_name
WHERE
    a.owner = UPPER(?) and a.TABLE_NAME=?
ORDER BY
    a.table_name, a.constraint_name", [$this->schema, $this->table->getName()]);

        foreach ($constraints as $constraint) {
            $result .= "| " . $constraint->constraint_name . " "
                . "| " . $constraint->column_name . " "
                . "| ";
            switch ($constraint->constraint_type) {
                case "C":
                case "V":
                    $result .= "check";
                    break;
                case "O":
                    $result .= "read-only";
                    break;
                case "P":
                    $result .= "pk";
                    break;
                case "R":
                    $result .= "fk";
                    break;
                case "U":
                    $result .= "unique";
                    break;
            }
            $result .= " | " . $constraint->search_condition . " |" . PHP_EOL;
        }

        return $result;
    }

    public function metadata(): string {
        return "| 문서코드 | " . $this->title() . " |
| ---- | --------------- |
| 테이블명 | " . $this->table->getName() . "           |
| 설명   |        |
| 관련문서 |                 |

";
    }

    public function contents(): string
    {
        return $this->metadata()
            . "###### 테이블" . PHP_EOL . PHP_EOL
            . $this->table() . PHP_EOL . PHP_EOL .
            "###### 인덱스" . PHP_EOL . PHP_EOL
            . $this->indexes() . PHP_EOL . PHP_EOL
            . "###### 제약조건" . PHP_EOL . PHP_EOL
            . $this->constraints() . PHP_EOL;
    }
